noemdeID\";\n                        break;\n                    case $email:\n                        $Update = \"UPDATE $tablename set Email=$Email WHERE ID=$GenoemdeID\";\n                        break;\n                    \n                    default:\n                        echo \"Error Update \". $GenoemdeID . $conn->error;\n                }   \n            $conn->query($Update);\n            \n    function Delete_ID($tablename, $GenoemdeID){\n        $Delete = \"DELETE FROM $tablename WHERE ID=$GenoemdeID\";\n            $conn->query($Delete);\n            if ($conn->query($sql) === TRUE) {\n                echo $GenoemdeID. \" deleted successfully\";\n            } else {\n                echo \"Error deleting\". $GenoemdeID . $conn->error;\n            }           \n        }\n        }\n        $conn = null;\n?>\n\n\n        \n    </body>\n</html>","undoManager":{"mark":-2,"position":100,"stack":[[{"start":{"row":41,"column":22},"end":{"row":42,"column":0},"action":"remove","lines":["",""],"id":388}],[{"start":{"row":41,"column":21},"end":{"row":41,"column":22},"action":"remove","lines":[","],"id":389}],[{"start":{"row":30,"column":33},"end":{"row":30,"column":34},"action":"insert","lines":["{"],"id":390}],[{"start":{"row":53,"column":12},"end":{"row":53,"column":13},"action":"remove","lines":[";"],"id":391}],[{"start":{"row":53,"column":12},"end":{"row":53,"column":13},"action":"insert","lines":[";"],"id":392}],[{"start":{"row":53,"column":13},"end":{"row":54,"column":0},"action":"insert","lines":["",""],"id":393}],[{"start":{"row":54,"column":0},"end":{"row":54,"column":1},"action":"insert","lines":["}"],"id":394}],[{"start":{"row":44,"column":4},"end":{"row":44,"column":49},"action":"remove","lines":["// use exec() because no results are returned"],"id":395}],[{"start":{"row":36,"column":4},"end":{"row":36,"column":26},"action":"remove","lines":["// sql to create table"],"id":396}],[{"start":{"row":33,"column":4},"end":{"row":33,"column":42},"action